import React from "react"

export default function Question (props) {
// color settings to answer options
    function styles(buttonState) {
        if (props.gameWon) {
            if (buttonState.guessed === "yes") {
                return {backgroundColor: "#29E636" }
            } else {
                return {backgroundColor: buttonState.guessed === "no" ? "#E46F8A" : buttonState.guessed === "correctAns"? "#73BEEF" : "transparent"}
            }
         } else {
            return {backgroundColor: buttonState.held ? "rgb(235, 195, 52)" : "transparent"}
        }
    }

    function hoverStyle () {
        return {backgroundColor: "#ABC6D8" }
    }

    return (
        <div>
            <div className="questionsPlace">
                <div className="questionBox">{props.question}</div>
                <div className="answers" >
                    <div className="ansBox" id={props.answers[0].id} name={props.name} style={styles(props.answers[0])} onMouseOver={hoverStyle()} onClick={props.toggle} >{props.answers[0].ans}</div>
                    <div className="ansBox" id={props.answers[1].id} name={props.name} style={styles(props.answers[1])} onMouseOver={hoverStyle()} onClick={props.toggle}>{props.answers[1].ans}</div>
                    <div className="ansBox" id={props.answers[2].id} name={props.name} style={styles(props.answers[2])} onMouseOver={hoverStyle()} onClick={props.toggle}>{props.answers[2].ans}</div>
                    <div className="ansBox" id={props.answers[3].id} name={props.name} style={styles(props.answers[3])} onMouseOver={hoverStyle()} onClick={props.toggle}>{props.answers[3].ans}</div>
                </div>
                <hr></hr>
            </div>
        </div>
    )
}
