import logo from './logo.svg';
import React from 'react';
import './App.css';
import Question from "./Question"

function App() {

const [ start, setStart]= React.useState(true)
const [questions, setQuestions] = React.useState()
const [answers, setAnswers] = React.useState()

function changeStart () {
  setStart(!start)
}

React.useEffect(() => {
  fetch("https://opentdb.com/api.php?amount=5&type=multiple")
  .then(res => res.json())
  .then(data => {
    const questionsArray = []
    const answersArray =[]
    for (let i=0; i<5; i++) {
      questionsArray.push(data.results[i].question)
      answersArray.push({
         correct: data.results[i].correct_answer,
         incorrect: data.results[i].incorrect_answers
      })
    }
    setQuestions(questionsArray)
    setAnswers(answersArray)
  })
},[])

const questionsRender = questions.map((element) => (
  <Question question={element} answers={[1,2,3,4]}/>
))



  return (
    <div>
        {(start) && (
          <div>
            <div className='container'> 
                <div className='title'>Quizzleee</div>
                <div className='info'>Check your overall knowledge?</div>
                <button className='button' onClick={changeStart}>Start Quiz</button>
              </div>
          </div> 
                 )}
        {(!start) && (
          <div>
            <div className='containerAnswers'> 
                {questionsRender}
                {/* <Question question={"pytanie drugie"} answers={['a','b','c','d']}/>
                <Question question={"pytanie trzecie"} answers={[1,2,3,4]}/>
                <Question question={"pytanie czwarte"} answers={[1,2,3,4]}/>
                <Question question={"pytanie piate"} answers={[1,2,3,4]}/> */}
                <button className='button' >Check</button>
              </div>
          </div> 
                 )}

    </div>
  );
}

export default App;
