import React from "react"

export default function Question (props) {

console.log(props.answers)

    function styles(held) {
        return {backgroundColor: held ? "rgb(235, 195, 52)" : "transparent"}
    }


    return (
        <div>
            <div className="questionBox"  >{props.question}</div>
            <div className="answers" >
                <div className="ansBox" id={props.answers[0].id} name={props.name} style={styles(props.answers[0].held)}  onClick={props.toggle} >{props.answers[0].ans}</div>
                <div className="ansBox" id={props.answers[1].id} name={props.name} style={styles(props.answers[1].held)}  onClick={props.toggle}>{props.answers[1].ans}</div>
                <div className="ansBox" id={props.answers[2].id} name={props.name} style={styles(props.answers[2].held)}  onClick={props.toggle}>{props.answers[2].ans}</div>
                <div className="ansBox" id={props.answers[3].id} name={props.name} style={styles(props.answers[3].held)}  onClick={props.toggle}>{props.answers[3].ans}</div>
            </div>
            <hr></hr>
        </div>
    )
}
