import React from "react"

export default function Question (props) {
    let id = props.id;
    let a = "a" + props.id
    let b = "b" + props.id
    let c = "c" + props.id
    let d = "d" + props.id
    const userAn = props.userAns


    const styles = {
       backgroundColor: props.checked ? "#222222" : "transparent"
    }

    function handleClick(event) {
        console.log(id)
        console.log(event.target.innerHTML)
        console.log(userAn)

        props.setUserAns(() => [
            ...userAn, { id: id, answer: event.target.innerHTML, checked: true}

        ])
        return props.toggle

    }


    return (
        <div>
            <div className="questionBox" >{props.question}</div>
            <div className="answers">
                <div className="ansBox" key={a} onClick={handleClick} >{props.answers[0]}</div>
                <div className="ansBox" key={b} onClick={handleClick}>{props.answers[1]}</div>
                <div className="ansBox" key={c} onClick={handleClick}>{props.answers[2]}</div>
                <div className="ansBox" key={d} onClick={handleClick}>{props.answers[3]}</div>
            </div>
            <hr></hr>
        </div>
    )
}
