import React from "react"

export default function Question (props) {
    let id = props.id;
    let a = "a" + props.id
    let b = "b" + props.id
    let c = "c" + props.id
    let d = "d" + props.id

    console.log(props)

    const styles = {
       backgroundColor: props.checked ? "#222222" : "transparent"
    }

    function handleClick(event) {
        console.log(id)
        const answer = event.target.innerHTML
        console.log(props.answers)
        console.log(event.target)
        console.log(answer)
        console.log(props.fullAnswers)
        const newAnsArr = []
        props.setAns(prevAns => {
                return prevAns.map(ans => {
                    console.log(ans)
                    if (ans === props.answers[id]){
                        return newAnsArr.push(ans.map((el) => {
                           return el.ans === answer ? {...el, held: !el.held} : el
                        }))
                } else {
                        newAnsArr.push(ans)
                    }
                    return newAnsArr
                })

                // } else {
                //   return {id: id, answer: event.target.innerHTML, checked: false}
                // }

            })
        }








    return (
        <div>
            <div className="questionBox" >{props.question}</div>
            <div className="answers">
                <div className="ansBox" style={styles} key={a} onClick={handleClick} >{props.answers[0].ans}</div>
                <div className="ansBox" style={styles} key={b} onClick={handleClick}>{props.answers[1].ans}</div>
                <div className="ansBox" style={styles} key={c} onClick={handleClick}>{props.answers[2].ans}</div>
                <div className="ansBox" style={styles} key={d} onClick={handleClick}>{props.answers[3].ans}</div>
            </div>
            <hr></hr>
        </div>
    )
}
