              tempArray.push({...flatArr[4*i], guessed: "yes"})
            } else {
              tempArray.push(flatArr[4*i+3])
            }
        } else {
          if (flatArr[4*i].ans === userAns[i] ) {
            tempArray.push({...flatArr[4*i], guessed: "no"})
          } else {
            tempArray.push(flatArr[4*i])
          }

