          }

          if (flatArr[4*i+3].ans === userAns[i] ) {
            tempArray.push({...flatArr[4*i], guessed: "no"})
          } else {
            tempArray.push(flatArr[4*i+3])
          }
        }
        arrayAns.push(tempArray)
    }

    // arrayCorrectAns.map ((el,i) => {
