import React from "react"

export default function Question (props) {
    let id = props.id;


    const styles = {
       backgroundColor: props.checked ? "#222222" : "transparent"
    }

    function handleClick(event) {
        console.log(id)
        const answer = event.target.innerHTML
        console.log(props.answers)
        console.log(event.target)
        console.log(answer)
        console.log(props.fullAnswers)
        const newAnsArr = []
        props.setAns(prevAns => {
                prevAns.map(ans => {
                    console.log(ans)
                    console.log(props.answers)
                    if (ans === props.answers){
                        const innerArr=[]
                        ans.map((el) => {
                            console.log(el)

                           return el.ans === answer ? innerArr.push({id: el.id, ans: el.ans, held: !el.held})
                            : innerArr.push(el)
                        })
                        newAnsArr.push(innerArr)
                } else {
                        newAnsArr.push(ans)
                    }
                    console.log(newAnsArr)
                    const final = newAnsArr.splice(4)
                    console.log(final)
                    return newAnsArr
                })

                // } else {
                //   return {id: id, answer: event.target.innerHTML, checked: false}
                // }

            })
        }








    return (
        <div>
            <div className="questionBox" >{props.question}</div>
            <div className="answers">
                <div className="ansBox" style={styles} key={props.key} onClick={handleClick} >{props.answers[0].ans}</div>
                <div className="ansBox" style={styles} key={props.key} onClick={handleClick}>{props.answers[1].ans}</div>
                <div className="ansBox" style={styles} key={props.key} onClick={handleClick}>{props.answers[2].ans}</div>
                <div className="ansBox" style={styles} key={props} onClick={handleClick}>{props.answers[3].ans}</div>
            </div>
            <hr></hr>
        </div>
    )
}
