import logo from './logo.svg';
import React from 'react';
import './App.css';
import Question from "./Question"

function App() {

const [ start, setStart]= React.useState(true)
const [questions, setQuestions] = React.useState()
const [answers, setAnswers] = React.useState()
const [userAns, setUserAns] = React.useState([])
const [ansShownScrren, setAnsShownScreen] = React.useState()
const [gameWon, setGameWon] = React.useState(false)
// changes start screen into game
function changeStart () {
  setStart(!start)
}

// data download from api
React.useEffect(() => {
  fetch("https://opentdb.com/api.php?amount=5&difficulty=easy&type=multiple")
  .then(res => res.json())
  .then(data => {

    const questionsArray = []
    const answersArray =[]
    for (let i=0; i<5; i++) {
      questionsArray.push({
        question: data.results[i].question,
        id: i
      })
      answersArray.push({
         correct: data.results[i].correct_answer,
         incorrect: data.results[i].incorrect_answers
      })
    }
    setQuestions(questionsArray)
    setAnswers(answersArray)

  })
  .then(data => { 
    console.log(answers)
    console.log(questions)
    // setAnsShownScreen(() => {
    // answers.map(el => mixAnswers(el))
  // })
})
},[])

// React.useEffect (() => {

// },[gameWon])

function mixAnswers (obj) {
    let finalAnsArr = []
    let newAnswers = [
      obj.correct, 
      obj.incorrect[0],
      obj.incorrect[1],
      obj.incorrect[2],
    ]
    //choose 1 ans randomly
    const choose1 = Math.floor(Math.random()*3)
    finalAnsArr.push(newAnswers[choose1])
    newAnswers.splice(choose1,1)
    //choose 2 ans randomly
    const choose2 = Math.floor(Math.random()*2)
    finalAnsArr.push(newAnswers[choose2])
    newAnswers.splice(choose2,1)
    //choose 3 ans randomly
    const choose3 = Math.floor(Math.random())
    finalAnsArr.push(newAnswers[choose3])
    newAnswers.splice(choose3,1)
    finalAnsArr.push(newAnswers[0])

    return finalAnsArr
}

function saveAns (id, e) {
  console.log(id)
  console.log(e)
  // setUserAns(()=> [
  //   ...userAns, { id: id, answer: 
  //   }
  // ])
}

function toggle () {
  setUserAns (prevAns => {
    console.log(prevAns)
    return prevAns.map((ans) => {
        return ans.checked === true ? {...ans, checked: !ans.checked} : ans
    })
})
}


// draws element onto DOM
  return (
    <div>
        {(start) && (
          <div>
            <div className='container'> 
                <div className='title'>Quizzleee</div>
                <div className='info'>Check your overall knowledge?</div>
                <button className='button' onClick={changeStart}>Start Quiz</button>
              </div>
          </div> 
                 )}
        {(!start) && (
          <div>
            <div className='containerAnswers'> 
                <Question 
                    question={questions[0].question} 
                    key={questions[0].id} 
                    answers={ansShownScrren[0]}
                    saveAns={saveAns()}
                    id={questions[0].id} 
                    setUserAns={setUserAns}
                    userAns = {userAns}
                    checked={userAns.checked}
                    toggle={() => toggle(questions[0].id)}/>
                {/* <Question 
                    question={questions[1].question} 
                    key={questions[1].id} 
                    answers={mixAnswers(answers[1])}
                    saveAns={saveAns()}
                    id={questions[1].id} />
                <Question 
                    question={questions[2].question} 
                    key={questions[2].id} 
                    answers={mixAnswers(answers[2])}
                    saveAns={saveAns()}
                    id={questions[2].id} />
                <Question 
                    question={questions[3].question} 
                    key={questions[3].id} 
                    answers={mixAnswers(answers[3])}
                    saveAns={saveAns()}
                    id={questions[3].id} />
                <Question 
                    question={questions[4].question} 
                    key={questions[4].id} 
                    answers={mixAnswers(answers[4])}
                    saveAns={saveAns()}
                    id={questions[4].id} /> */}
                <button className='button' >Check</button>
              </div>
          </div> 
                 )}

    </div>
  );
}

export default App;
